import 'package:flutter/material.dart';
import 'package:cached_network_image/cached_network_image.dart';

class Overview extends StatelessWidget {
  const Overview({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            // Display an image from assets
            Image.asset(
              'assets/images/a.png',
              width: 200, // You might want to set dimensions for better control
              height: 200,
            ),
            const SizedBox(height: 10), // Adds some space between widgets

            // Display an image from a network URL using CachedNetworkImage
            CachedNetworkImage(
              imageUrl: 'data:image/jpeg;base64,',
              placeholder: (context, url) => CircularProgressIndicator(), // Placeholder while image is loading
              errorWidget: (context, url, error) => Icon(Icons.error), // Error widget if image fails to load
              width: 200,
              height: 200,
              fit: BoxFit.cover,
            ),
            const SizedBox(height: 10),

            // You can add other widgets below the images
            const Text('This is an image above'),
            const SizedBox(height: 10),
            const Text('This is a changing image'),
          ],
        ),
      ),
    );
  }
}